// Sample movie data
const movies = [
  { title: "The Shawshank Redemption", year: 1994, director: "Frank Darabont", image: "data:image/jpeg;base64," },
  { title: "The Godfather", year: 1972, director: "Francis Ford Coppola", image: "data:image/jpeg;base64," },
  { title: "Pulp Fiction", year: 1994, director: "Quentin Tarantino", image: "data:image/jpeg;base64," },
  // Add more movies here
];

// Function to display movies
function displayMovies() {
  const moviesContainer = document.getElementById("movies-container");
  moviesContainer.innerHTML = ""; // Clear previous content

  movies.forEach(movie => {
    const movieCard = document.createElement("div");
    movieCard.classList.add("movie-card");

    const image = document.createElement("img");
    image.src = movie.image;
    movieCard.appendChild(image);

    const title = document.createElement("h2");
    title.textContent = movie.title;
    movieCard.appendChild(title);

    const director = document.createElement("p");
    director.textContent = "Director: " + movie.director;
    movieCard.appendChild(director);

    const year = document.createElement("p");
    year.textContent = "Year: " + movie.year;
    movieCard.appendChild(year);

    moviesContainer.appendChild(movieCard);
  });
}

// Function to handle search button click
function handleSearch() {
  const searchInput = document.getElementById("search-input");
  const searchTerm = searchInput.value.toLowerCase();

  const filteredMovies = movies.filter(movie =>
    movie.title.toLowerCase().includes(searchTerm) ||
    movie.director.toLowerCase().includes(searchTerm)
  );

  displayMovies(filteredMovies);
}

// Attach event listener to search button
const searchButton = document.getElementById("search-button");
searchButton.addEventListener("click", handleSearch);

// Display all movies on initial load
displayMovies();
